import React, { Component } from "react";
import QrReader from "react-qr-reader";
const axios = require("axios").default;
const axiosDiemDanh = axios.create({
  baseURL: "http://localhost:2212/api/"
});
class QrScanner extends Component {
  state = {
    result: "No result",
    current_user: {},
    checkins: []
  };
  getUserInfo = _id => {
    axiosDiemDanh.get(`/user/${_id}`).then(res => {
      if (res.success) {
        return res.data;
      } else return {};
    });
  };
  createCheckin = _user_id => {
    axiosDiemDanh
      .post("/checkin", { user_id: _user_id })
      .then(res => {
        console.log(res);
        if (res.success) {
          return this.getCheckins();
        } 
      })
      .catch(err => console.log(err));
  };
  getCheckins = () => {
    axiosDiemDanh
      .get("/checkins")
      .then(res => { 
        this.setState({ checkins: res.data });
      })
      .catch(err => console.log(err));
  };
  handleScan = data => {
    if (data) {
      this.getUserInfo(data);
      this.createCheckin(data);
      this.setState({
        result: data
      });
    }
  };
  handleError = err => {
    console.error(err);
  };
  render() {
    console.log(this.state);
    return (
      <div>
        <QrReader
          delay={5000}
          onError={this.handleError}
          onScan={this.handleScan}
          style={{ width: "20%", height: "20%" }}
        />
        <p>{this.state.result}</p>
        <p>{this.state.current_user.name}</p>
      </div>
    );
  }
}
export default QrScanner;
